const modal = document.querySelector('.modal');
const buttonClose = modal.querySelector('.modal__btn-close');
const button = document.querySelector('.search__link');


button.addEventListener('click', (evt) => {
  evt.preventDefault();
  modal.classList.toggle('modal--open');
});
buttonClose.addEventListener('click', (evt) => {
  modal.classList.toggle('modal--open');
});
modal.addEventListener('click', (evt) => {
  if (evt.target === evt.currentTarget) modal.classList.toggle('modal--open');
});
